import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import css from './Modal.module.css';

export default function ShowModal({ handleClose }) {
  const [contact, setContact] = useState({
    name: '',
    email: '',
    phone: '',
  });
  const [show, setShow] = useState(false);
  const notify = () => {
    toast.success(
      'Данные успешно отправлены. Мы свяжемся с Вами в ближайшее время',
      { theme: 'dark', position: toast.POSITION.TOP_CENTER }
    );
    if (name === '') {
      alert('no name');
    }
  };
  const handleSubmit = () => {
    notify();
    setShow(false);
  };
  return (
    <>
      <Modal show={show} onHide={handleClose} className={css.modal__backdrop}>
        <Modal.Header></Modal.Header>
        <Modal.Body className={css.modal__body}>
          <div className={css.modal__items}>
            <ul className={css.items}>
              <li className={css.modal__item}>
                <h2 className={css.modal__item__title}>Имя</h2>
                <input
                  type="text"
                  name="name"
                  pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                  title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
                  required
                  value={contact.name}
                  className={css.modal__input}
                />
              </li>
              <li className={css.modal__item}>
                <h2 className={css.modal__item__title}>
                  Телеграмм/контактный номер
                </h2>
                <input
                  type="text"
                  name="phone"
                  required
                  value={contact.phone}
                  className={css.modal__input}
                />
              </li>
              <li className={css.modal__item}>
                <h2 className={css.modal__item__title}>E-mail</h2>

                <input
                  type="email"
                  name="email"
                  required
                  value={contact.email}
                  className={css.modal__input}
                />
              </li>
              <li className={css.modal__item}>
                <Button
                  type="submit"
                  onClick={handleSubmit}
                  className={css.button}
                >
                  Отправить
                </Button>
              </li>
              <li className={css.modal__item}>
                <Button
                  type="button"
                  onClick={handleClose}
                  className={css.button__close}
                >
                  Закрыть
                </Button>
              </li>
            </ul>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
}
