import css from './Block.module.css';
import give_arrow from '../../images/give_arrow.png';
// import Bitcoin from '../../images/Bitcoin.png';
// import Ethereum from '../../images/Ethereum.png';
// import USDT from '../../images/USDT.png';

const defaultCurrencies = [
  {
    type: 'Bitcoin',
    value: 'Bitcoin',
    img: '../../images/Bitcoin.png',
  },
  {
    type: 'Ethereum',
    value: 'Ethereum',
  },
  {
    type: 'USDT TRC20',
    value: 'USDT TRC20',
  },
  {
    type: 'USDT ERC20',
    value: 'USDT ERC20',
  },
  {
    type: 'USD',
    value: 'USD',
  },
  {
    type: 'EUR',
    value: 'EUR',
  },
  {
    type: 'PLN',
    value: 'PLN',
  },
];

export const Block = ({ value, currency, onChangeCurrency }) => {
  // const onChangeCurrency = event => {
  //   event.preventDefault();
  // if () {

  // }
  // };

  return (
    <>
      <div className={css.give__currency}>
        <div className={css.crypto__thumb}>
          <div className={css.title}>
            ОТДАЁТЕ:
            <img src={give_arrow} width="24" height="28" alt="" />
          </div>
          <ul className={css.crypto__currencies__list}>
            {defaultCurrencies.map(({ key, value }) => (
              <li key={value}>
                <p className={css.cryptocurrency__type}>
                  <span className={css.currency__img}>
                    <img
                      src={defaultCurrencies.img}
                      width="36"
                      height="36"
                      alt=""
                    />
                  </span>
                  {value}
                </p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
};
export default Block;
